"use client";

import { useState } from "react";
import { MapPin, Briefcase, DollarSign } from "lucide-react";
import { useGetJobsQuery } from "../services/JobApi";
import JobCard from "./JobCard";
import ApplicationForm from "./ApplicationForm";
import AppliedJobs from "./AppliedJobs";
import { JobSkeleton } from "./JobSkeleton";
import {
  AppBar,
  Toolbar,
  Typography,
  Container,
 
  TextField,
  Button,
  Card,
} from "@mui/material";

export default function JobBoard() {
  const [selectedJob, setSelectedJob] = useState(null);
  const [searchParams, setSearchParams] = useState({
    location: "",
    experience: "",
    salary: "",
  });

  const { data: jobs, isLoading, refetch } = useGetJobsQuery(searchParams);

  const handleSearch = (e) => {
    e.preventDefault();
    refetch();
  };

  return (
    <div style={{ minHeight: "100vh", backgroundColor: "#121212", color: "white" }}>
      {/* Header */}
      <AppBar position="static" sx={{ backgroundColor: "#1E1E1E" }}>
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Eclipse
          </Typography>
          {["Find Job", "Messages", "Hiring", "Community", "FAQ"].map((item) => (
            <Button key={item} color="inherit">
              {item}
            </Button>
          ))}
        </Toolbar>
      </AppBar>

      <Container sx={{ py: 4 }}>
        <Grid container spacing={3}>
          {/* Job Listings */}
          <Grid item xs={12} md={8}>
            <Card sx={{ backgroundColor: "#1E1E1E", padding: 2, marginBottom: 2 }}>
              <form onSubmit={handleSearch}>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={4}>
                    <TextField
                      fullWidth
                      variant="outlined"
                      placeholder="Location"
                      InputProps={{
                        startAdornment: <MapPin style={{ marginRight: 8, color: "gray" }} />,
                      }}
                      sx={{
                        input: { color: "white" },
                        "& .MuiOutlinedInput-root": {
                          "& fieldset": { borderColor: "gray" },
                          "&:hover fieldset": { borderColor: "white" },
                        },
                      }}
                      value={searchParams.location}
                      onChange={(e) => setSearchParams({ ...searchParams, location: e.target.value })}
                    />
                  </Grid>
                  <Grid item xs={12} sm={4}>
                    <TextField
                      fullWidth
                      type="number"
                      variant="outlined"
                      placeholder="Experience (years)"
                      InputProps={{
                        startAdornment: <Briefcase style={{ marginRight: 8, color: "gray" }} />,
                      }}
                      sx={{
                        input: { color: "white" },
                        "& .MuiOutlinedInput-root": {
                          "& fieldset": { borderColor: "gray" },
                          "&:hover fieldset": { borderColor: "white" },
                        },
                      }}
                      value={searchParams.experience}
                      onChange={(e) => setSearchParams({ ...searchParams, experience: e.target.value })}
                    />
                  </Grid>
                  <Grid item xs={12} sm={4}>
                    <TextField
                      fullWidth
                      type="number"
                      variant="outlined"
                      placeholder="Salary (k/year)"
                      InputProps={{
                        startAdornment: <DollarSign style={{ marginRight: 8, color: "gray" }} />,
                      }}
                      sx={{
                        input: { color: "white" },
                        "& .MuiOutlinedInput-root": {
                          "& fieldset": { borderColor: "gray" },
                          "&:hover fieldset": { borderColor: "white" },
                        },
                      }}
                      value={searchParams.salary}
                      onChange={(e) => setSearchParams({ ...searchParams, salary: e.target.value })}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <Button fullWidth type="submit" variant="contained" color="primary">
                      Search
                    </Button>
                  </Grid>
                </Grid>
              </form>
            </Card>

            <Grid container spacing={2}>
              {isLoading ? (
                Array.from({ length: 3 }).map((_, index) => (
                  <Grid item xs={12} key={index}>
                    <JobSkeleton />
                  </Grid>
                ))
              ) : (
                jobs?.map((job) => (
                  <Grid item xs={12} key={job.id}>
                    <JobCard job={job} onApply={() => setSelectedJob(job)} />
                  </Grid>
                ))
              )}
            </Grid>
          </Grid>

          {/* Sidebar */}
          <Grid item xs={12} md={4}>
            <ApplicationForm job={selectedJob} onClose={() => setSelectedJob(null)} />
            <AppliedJobs />
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}
